import React from "react";

const HeroSection = () => {
  return (
    <section className="bg-gray-50 py-12 text-center">
      <h2 className="text-4xl font-bold mb-4">Master Technologies Faster</h2>
      <p className="text-gray-600 max-w-xl mx-auto">
        Explore quick, bite-sized learning modules and stay ahead in technology.
      </p>
    </section>
  );
};

export default HeroSection;
