import React, { useState } from "react";
import HeroSection from "../components/HeroSection";
import TechButton from "../components/TechButton";
import "./styles/Home.css";

const technologies = [
  { name: "React", color: "#61DAFB" },
  { name: "Java", color: "#f89820" },
  { name: "Python", color: "#3776AB" },
  { name: "Node.js", color: "#3C873A" },
  { name: "MongoDB", color: "#4DB33D" },
  { name: "HTML5", color: "#E34C26" },
  { name: "CSS3", color: "#264de4" },
  { name: "JavaScript", color: "#F0DB4F" },
  { name: "Reafct", color: "#61DAFB" },
  { name: "Jarva", color: "#f89820" },
  { name: "Pytrhon", color: "#3776AB" },
  { name: "Nodfe.js", color: "#3C873A" },
  { name: "MonfgoDB", color: "#4DB33D" },
  { name: "HTMrfL5", color: "#E34C26" },
  { name: "CSSf3", color: "#264de4" },
  { name: "JavaffeScript", color: "#F0DB4F" },
];

const Home = () => {
  const [searchQuery, setSearchQuery] = useState("");

  const filteredTechs = technologies.filter((tech) =>
    tech.name.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <>
      <HeroSection searchQuery={searchQuery} setSearchQuery={setSearchQuery} />
      <section className="tech-buttons-grid">
        {filteredTechs.map((tech) => (
          <TechButton
            key={tech.name}
            label={tech.name}
            color={tech.color}
          />
        ))}
        {filteredTechs.length === 0 && (
          <p style={{ textAlign: "center", marginTop: "20px" }}>
            No technology found.
          </p>
        )}
      </section>
    </>
  );
};

export default Home;
