// src/components/HeroSection.jsx

import React from "react";

const HeroSection = () => {
  return (
    <section className="bg-gray-100 text-center py-10">
      <h2 className="text-3xl font-bold mb-4">Welcome to DLearning Center</h2>
      <p className="text-gray-700 max-w-xl mx-auto">
        Learn technology skills quickly and effectively with our bite-sized courses and resources.
      </p>
    </section>
  );
};

export default HeroSection;
